using ReactiveUI;
using Noggog.WPF;
using System.Reactive.Disposables;

namespace Mutagen.Bethesda.WPF.Reflection
{
    public class AutogeneratedSettingViewBase : NoggogUserControl<ReflectionSettingsVM> { }

    /// <summary>
    /// Interaction logic for AutogeneratedSettingView.xaml
    /// </summary>
    public partial class AutogeneratedSettingView : AutogeneratedSettingViewBase
    {
        public AutogeneratedSettingView()
        {
            InitializeComponent();
            this.WhenActivated(disposable =>
            {
                this.WhenAnyValue(x => x.ViewModel!.SelectedSettings)
                    .BindTo(this, x => x.MainGrid.DataContext)
                    .DisposeWith(disposable);
            });
        }
    }
}
